@layer base {
  :root {
    --radius: 0.5rem;

    /* Paleta "Pastel Dreams" - variables CSS */
    --background: 35 30% 98%; /* Crema c√°lido */
    --foreground: 348 25% 40%; /* Marr√≥n principal */

    --primary: 354 100% 85%; /* Rosa pastel */
    --primary-foreground: 348 25% 40%;

    --secondary: 210 100% 85%; /* Celeste suave */
    --secondary-foreground: 210 50% 35%;

    --accent: 45 100% 85%; /* Amarillo suave */
    --accent-foreground: 45 30% 45%;

    --muted: 35 30% 98%; /* Crema c√°lido */
    --muted-foreground: 348 25% 60%;

    --card: 0 0% 100%;
    --card-foreground: 348 25% 40%;

    --popover: 0 0% 100%;
    --popover-foreground: 348 25% 40%;

    --border: 348 20% 90%;
    --input: 348 20% 90%;
    --ring: 354 100% 85%;

    --destructive: 0 84.2% 60.2%;
