  }

  .gradient-pastel {
    background: linear-gradient(135deg, #FFB3BA 0%, #B3D9FF 50%, #FFE5B3 100%);
  }

  .text-gradient {
    @apply bg-gradient-to-r from-primary via-secondary to-accent bg-clip-text text-transparent;
  }
}



